import random

def karate_chop(lst, target):
    if len(lst) == 0:
        return -1
    if len(lst) == 1:
        if lst[0] == target:
            return 0
        else:
            return -1
    mid = len(lst)//2
    if lst[mid] == target:
        return mid
    elif lst[mid] > target:
        return karate_chop(lst[:mid], target)
    else:
        index = karate_chop(lst[mid:], target)
        if index == -1:
            return -1
        else:
            return mid + index


