import requests
from bs4 import BeautifulSoup

url = 'https://growingglobalcitizens.com/the-best-places-to-learn-a-language-abroad/'

destinations = {
    'Indonesian': ['Salatiga, Java, Indonesia'],
    'European Portuguese': ['Lisbon, Portugal'],
    'Brazilian Portuguese': ['Maceio, Brazil', 'Fortaleza, Brazil'],
    'Spanish': ['Madrid, Spain', 'Medellin, Colombia', 'Sucre, Bolivia'],
    'Castellano Spanish': ['Zaragoza, Spain']
}


def get_best_ways_to_learn(language, destination):
    query = f'best ways to learn {language} in {destination}'
    google_url = f'https://www.google.com/search?q={query}'
    response = requests.get(google_url)
    soup = BeautifulSoup(response.content, 'html.parser')
    return soup.find_all('div', class_='BNeawe iBp4i AP7Wnd')

if __name__ == '__main__':
    for language in destinations:
        print(f'Best ways to learn {language}:')
        for destination in destinations[language]:
            results = get_best_ways_to_learn(language, destination)
            if results:
                print(f'{destination}: {results[0].text}')
        print('\n')
