{
  "$schema": "http://json-schema.org/draft-07/schema#",
  "type": "object",
  "properties": {
    "thoughts": {
      "type": "object",
      "properties": {
        "text": {
          "type": "string",
          "description": "thoughts"
        },
        "reasoning": {
          "type": "string"
        },
        "plan": {
          "type": "string",
          "description": "- short bulleted\n- list that conveys\n- long-term plan"
        },
        "criticism": {
          "type": "string",
          "description": "constructive self-criticism"
        },
        "speak": {
          "type": "string",
          "description": "thoughts summary to say to user"
        }
      },
      "required": [
        "text",
        "reasoning",
        "plan",
        "criticism",
        "speak"
      ],
      "additionalProperties": false
    },
    "command": {
      "type": "object",
      "properties": {
        "name": {
          "type": "string"
        },
        "args": {
          "type": "object"
        }
      },
      "required": [
        "name",
        "args"
      ],
      "additionalProperties": false
    }
  },
  "required": [
    "thoughts",
    "command"
  ],
  "additionalProperties": false
}

{
    "thoughts": {
        "text": "Opening the map of the Brno Reservoir trail and surrounding area in Firefox.",
        "reasoning": "The user has planned their route and stops using a performance evaluation resource, so opening the map in Firefox will give them a visual representation of the area and help them navigate.",
        "plan": "- Open Firefox\n- Navigate to the map URL: https://mapy.cz/s/3vJX3",
        "criticism": "I should have checked if Firefox is installed before attempting to open it.",
        "speak": "I am opening the map of the Brno Reservoir trail and surrounding area in Firefox."
    },
    "command": {
        "name": "execute_shell",
        "args": {
            "command_line": "firefox https://mapy.cz/s/3vJX3"
        }
    }
}
